svg.script:
window.onload = function () {
console.log("Loaded!");
var pathEl = document.querySelector("#animatablePath");
var currentRotation = 0;
var anim = function () {
currentRotation++;
currentRotation %= 72;
pathEl.setAttribute("transform", "rotate(" + currentRotation + ")");
};
setInterval(anim, 20);
};
